            if (d[i] + 1 + d[j] == 3) {
                cnt[i]++;
                cnt[j]++;
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans += (cnt[i] - 1) / 2;
    }

