```python
from collections import deque

def bfs(graph, start):
    visited = [False] * len(graph)
    queue = deque()
    queue.append(start)
    visited[start] = True
    level = 0
    while queue:
        for _ in range(len(queue)):
            node = queue.popleft()
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    queue.append(neighbor)
                    visited[neighbor] = True
        level += 1
    return level

def dfs(graph, start, visited, level):
    visited[start] = True
    for neighbor in graph[start]:
        if not visited[neighbor]:
            dfs(graph, neighbor, visited, level + 1)

def max_edges(n, m, edges):
    graph = [[] for _ in range(n + 1)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (n + 1)
    max_level = 0
    for i in range(1, n + 1):
        if not visited[i]:
            level = bfs(graph, i)
            max_level = max(max_level, level)

    max_edges = 0
    for i in range(1, n + 1):
        visited = [False] * (n + 1)
        dfs(graph, i, visited, 0)
        for j in range(1, n + 1):
            if visited[j] and j != i:
                for k in range(1, n + 1):
                    if visited[k] and k != i and k != j:
                        if k in graph[j]:
                            graph[j].remove(k)
                            graph[k].remove(j)
        for i in range(1, n + 1):
            for j in range(i + 1, n + 1):
                if j in graph[i]:
                    graph[i].remove(j)
                    graph[j].remove(i)
        max_edges += (max_level - 2) * (n - max_level) + (n - max_level) // 2

    return m + max_edges

n, m = map(int, input().split())
edges = []
for _ in range(m):
    u, v = map(int, input().split())
    edges.append((u, v))
print(max_edges(n, m, edges))
