#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long nCr(long long n, long long r) {
    if (r > n - r) r = n - r;
    long long ans = 1;
    for (long long i = 0; i < r; i++) {
        ans = (ans * (n - i)) % MOD;
        ans = (ans * powMod(i + 1, MOD - 2, MOD)) % MOD;
    }
    return ans;
}

long long powMod(long long a, long long b, long long mod) {
    long long res = 1;
    while (b) {
        if (b & 1) res = (res * a) % mod;
        a = (a * a) % mod;
        b >>= 1;
    }
    return res;
}

int main() {
    long long A, B;
    scanf("%lld %lld", &A, &B);
    long long ans = 0;
    for (long long i = 0; i <= A + B; i++) {
        long long j = A + B - i;
        if (i < A && j < B) continue;
        ans = (ans + nCr(A + B, i) * nCr(A + B - i, A)) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
