```java
import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 4005;

    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int A = Integer.parseInt(st.nextToken());
        int B = Integer.parseInt(st.nextToken());

        fac[0] = 1;
        for (int i = 1; i <= A + B; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[A + B] = pow(fac[A + B], MOD - 2);
        for (int i = A + B - 1; i >= 1; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }

        long ans = 0;
        for (int i = 1; i <= A + B; i++) {
            for (int j = i + 1; j <= A + B; j++) {
                long tmp = C(A + B, i - 1) * C(A + B - i, j - i - 1) % MOD * C(A + B - j, A) % MOD;
                ans = (ans + tmp) % MOD;
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long C(int n, int m) {
        if (n < m) return 0;
        return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
