#define N 60

char ans[N];
int x, y, z;

void solve(int x, int y, int z, int len) {
    if (len == 0) return;
    if (x > 0) {
        ans[len - 1] = 'a';
        solve(x - 1, y, z, len - 1);
    } else if (y > 0) {
        ans[len - 1] = 'b';
        solve(x, y - 1, z, len - 1);
    } else {
        ans[len - 1] = 'c';
        solve(x, y, z - 1, len - 1);
    }
}

int main() {
    int i, j, k, len;
    char s[N];

    scanf("%d %d %d", &x, &y, &z);
    len = x + y + z;
    solve(x, y, z, len);
    for (i = 0; i < len; i++) {
        for (j = i + 1; j < len; j++) {
            if (ans[j] < ans[i]) break;
            if (ans[j] > ans[i]) {
                char temp = ans[i];
                ans[i] = ans[j];
                ans[j] = temp;
            }
        }
    }
    for (i = 0; i < len; i++) {
        s[i] = ans[i];
    }
    s[len] = '\0';
    printf("%s\n", s);
