import itertools

def f(s):
    return ''.join(sorted(s))

def solve(X, Y, Z):
    max_len = X + Y + Z
    max_str = ''
    for p in itertools.permutations('abc', max_len):
        str_ = ''.join(p[:X]) + 'a' * X + ''.join(p[X:X+Y]) + 'b' * Y + ''.join(p[X+Y:X+Y+Z]) + 'c' * Z
