import java.util.*;
import java.io.*;

public class Main {
    static int X, Y, Z;
    static char[] ans = new char[100];
    static int len = 0;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        X = Integer.parseInt(st.nextToken());
        Y = Integer.parseInt(st.nextToken());
        Z = Integer.parseInt(st.nextToken());
        solve(0, 0, 0);
        System.out.println(new String(ans, 0, len));
    }

    static void solve(int x, int y, int z) {
        if (x == X && y == Y && z == Z) {
            len = Math.max(len, Math.min(x, y, z) * 3);
            return;
        }
        if (x < X) {
            ans[len++] = 'a';
            solve(x + 1, y, z);
            if (len > 0) len--;
        }
        if (y < Y) {
            ans[len++] = 'b';
            solve(x, y + 1, z);
            if (len > 0) len--;
        }
        if (z < Z) {
            ans[len++] = 'c';
            solve(x, y, z + 1);
            if (len > 0) len--;
        }
    }
}
