#define MAXN 100005
#define MAXM 30

typedef struct {
    int a, b;
} Node;

int cmp(const void *a, const void *b) {
    Node *x = (Node *)a;
    Node *y = (Node *)b;
    return y->b - x->b;
}

int main() {
    int n, k;
    scanf("%d%d", &n, &k);
    Node a[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d%d", &a[i].a, &a[i].b);
    }
    qsort(a, n, sizeof(Node), cmp);
    int ans = 0;
    for (int i = 0; i < n; i++) {
        int tmp = k;
        for (int j = 0; j < MAXM; j++) {
            if (tmp & (1 << j)) {
                if (a[i].a & (1 << j)) {
                    ans += a[i].b;
                    break;
                }
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
