struct Point {
    long long x, y;
};

Point operator+(const Point& a, const Point& b) {
    return {a.x + b.x, a.y + b.y};
}

Point operator-(const Point& a, const Point& b) {
    return {a.x - b.x, a.y - b.y};
}

Point operator*(const Point& a, long long b) {
    return {a.x * b, a.y * b};
}

long long cross(const Point& a, const Point& b) {
    return a.x * b.y - a.y * b.x;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<Point> a(n);
    for (int i = 0; i < n; ++i) {
        long long x, y, c;
        cin >> x >> y >> c;
        a[i] = {x, y};
    }

    vector<Point> b(n);
    for (int i = 0; i < n; ++i) {
        b[i] = a[i] * cross(a[i], a[(i + 1) % n]);
    }

    long long x = 0, y = 0;
    for (int i = 0; i < n; ++i) {
        x += b[i].x;
        y += b[i].y;
    }

    x /= accumulate({b.begin(), b.end()}, b.end(), 0LL, [](long long a, const Point& b) { return a + b.x; });
    y /= accumulate({b.begin(), b.end()}, b.end(), 0LL, [](long long a, const Point& b) { return a + b.y; });

    cout << fixed << setprecision(9) << x << ' ' << y << '\n';

    return 0;
}
