        return l1.a * l2.b - l1.b * l2.a;
    }

    static Point intersection(Line l1, Line l2) {
        long a1 = l1.a, b1 = l1.b, c1 = l1.c;
        long a2 = l2.a, b2 = l2.b, c2 = l2.c;
        long x = (b2 * c1 - b1 * c2);
        long y = (a1 * c2 - a2 * c1);
        return new Point(x, y);
    }

    static void solve(FastScanner in, PrintWriter out) {
        int n = in.nextInt();
        Line[] lines = new Line[n + 2];
        for (int i = 0; i < n; i++) {
            lines[i] = new Line(in.nextLong(), in.nextLong(), in.nextLong());
        }
        lines[n] = new Line(1, 0, 0);
        lines[n + 1] = new Line(0, 1, 0);
        long[] x = new long[n + 2];
        long[] y = new long[n + 2];
        for (int i = 0; i < n + 2; i++) {
            x[i] = lines[i].c;
            y[i] = 0;
        }
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                Point p = intersection(lines[i], lines[j]);
                x[i] += p.x;
                x[j] += p.x;
                y[i] += p.y;
                y[j] += p.y;
            }
        }
        long minX = Long.MAX_VALUE;
        long minY = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            if (x[i] < minX) {
                minX = x[i];
                minY = y[i];
            } else if (x[i] == minX) {
                if (y[i] < minY) {
                    minY = y[i];
                }
            }
        }
        out.println(minX + " " + minY);
    }

    public static void main(String[] args) {
        FastScanner in = new FastScanner();
        PrintWriter out = new PrintWriter(System.out);
        int t = 1;
        while (t-- > 0) {
            solve(in, out);
        }
