#include <math.h>

typedef long long ll;

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int q;
    scanf("%d", &q);
    while (q--) {
        int a, m;
        scanf("%d %d", &a, &m);
        if (a == 1) {
            printf("1\n");
            continue;
        }
        int g = gcd(a, m);
        if (m % g != 1) {
            printf("-1\n");
            continue;
        }
        m /= g;
        a /= g;
        int k = 1;
        while (1) {
            ll x = powll(a, k, m);
            if (x == k) {
                printf("%d\n", k * g);
                break;
            }
            k++;
        }
    }
    return 0;
}

ll powll(ll a, ll b, ll mod) {
    ll res = 1;
    while (b) {
        if (b & 1) res = (res * a) % mod;
        a = (a * a) % mod;
        b >>= 1;
    }
    return res;
}
