import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) throws IOException {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int q = sc.nextInt();
        while (q-- > 0) {
            long a = sc.nextLong();
            long m = sc.nextLong();
            if (a == 1) {
                out.println(1);
                continue;
            }
            long phi = eulerTotient(m);
            if (gcd(a, m) != 1) {
                out.println(-1);
                continue;
            }
            long k = 1;
            while (k * phi + 1 <= 2 * (long) 1e18) {
                if (powMod(a, k * phi, m) == k) {
                    out.println(k);
                    break;
                }
                k++;
            }
            if (k > 2 * (long) 1e18) {
                out.println(-1);
            }
        }
        out.close();
    }

    static long gcd(long a, long b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }

    static long eulerTotient(long n) {
        long result = n;
        for (long p = 2; p * p <= n; p++) {
            if (n % p == 0) {
                while (n % p == 0) n /= p;
                result -= result / p;
            }
        }
        if (n > 1) result -= result / n;
        return result;
    }

    static long powMod(long a, long b, long mod) {
        long res = 1;
        a %= mod;
        while (b > 0) {
            if ((b & 1) == 1) res = (res * a) % mod;
            a = (a * a) % mod;
            b >>= 1;
        }
        return res;
    }
}
