        } else {
            bool ok = true;
            for (char c : row[i]) {
                if (c != *row[i].begin() || c != *row[i].rbegin()) {
                    ok = false;
                    break;
                }
            }
            if (!ok) {
                return false;
            }
        }
    }

    for (int j = 0; j < W; j++) {
        if (col[j].size() % 2 == 1) {
            if (col[j].size() > 2) {
                return false;
            }
        } else {
            bool ok = true;
            for (char c : col[j]) {
                if (c != *col[j].begin() || c != *col[j].rbegin()) {
                    ok = false;
                    break;
                }
            }
            if (!ok) {
                return false;
            }
        }
    }

    return true;
}

int main() {
    int H, W;
    cin >> H >> W;

    vector<string> A(H);
    for (int i = 0; i < H; i++) {
        cin >> A[i];
    }

    if (check(A)) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
