from collections import Counter

def is_palindrome(s):
    return s == s[::-1]

def check(matrix):
    h, w = len(matrix), len(matrix[0])
    row_counts = [Counter() for _ in range(h)]
    col_counts = [Counter() for _ in range(w)]

    for i in range(h):
        for j in range(w):
            row_counts[i][matrix[i][j]] += 1
            col_counts[j][matrix[i][j]] += 1

    for i in range(h):
        if not is_palindrome(''.join([k * v for k, v in row_counts[i].items()])):
            return False
    for j in range(w):
        if not is_palindrome(''.join([k * v for k, v in col_counts[j].items()])):
            return False
    return True

h, w = map(int, input().split())
matrix = [input() for _ in range(h)]
print("Yes" if check(matrix) else "No")
