public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int H = Integer.parseInt(st.nextToken());
        int W = Integer.parseInt(st.nextToken());
        char[][] grid = new char[H][W];
        for (int i = 0; i < H; i++) {
            String row = br.readLine();
            for (int j = 0; j < W; j++) {
                grid[i][j] = row.charAt(j);
            }
        }
        Set<Character> rowSet = new HashSet<>();
        Set<Character> colSet = new HashSet<>();
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                rowSet.add(grid[i][j]);
                colSet.add(grid[j][i]);
            }
        }
        for (int i = 0; i < H; i++) {
            if (!canFormPalindrome(grid[i], rowSet)) {
                System.out.println("No");
                return;
            }
        }
        for (int j = 0; j < W; j++) {
            if (!canFormPalindrome(getCol(grid, j), colSet)) {
                System.out.println("No");
                return;
            }
        }
        System.out.println("Yes");
    }

    public static boolean canFormPalindrome(char[] arr, Set<Character> set) {
        int count = 0;
        for (char c : arr) {
            if (set.contains(c)) {
                count++;
            }
        }
        return count % 2 == 0;
    }

    public static char[] getCol(char[][] grid, int col) {
        char[] colArr = new char[grid.length];
        for (int i = 0; i < grid.length; i++) {
            colArr[i] = grid[i][col];
        }
        return colArr;
    }
}
