#define MOD 998244353
#define N 100005
#define M 100005

int n, m;
char a[N], b[N], c[N], d[N];
int f[N][M], g[N][M];

int main() {
    scanf("%d %d", &n, &m);
    scanf("%s %s %s %s", a, b, c, d);
    for (int i = 1; i <= n; i++) {
        f[i][0] = (a[i - 1] == '1');
        g[i][m + 1] = (b[i - 1] == '1');
    }
    for (int j = 1; j <= m; j++) {
        f[0][j] = (c[j - 1] == '1');
        g[n + 1][j] = (d[j - 1] == '1');
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            f[i][j] = (f[i - 1][j] + f[i][j - 1] - f[i - 1][j - 1] + g[i][j + 1] + g[i + 1][j] - g[i + 1][j + 1]) % MOD;
        }
    }
    int ans = 0;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= m; j++) {
            ans = (ans + f[i][j]) % MOD;
        }
    }
    printf("%d\n", ans);
    return 0;
