    vector<int> B(n + 2, 0);
    vector<int> C(m + 2, 0);
    vector<int> D(m + 2, 0);

    for (int i = 1; i <= n; i++) {
        A[i] = (a[i - 1] == '1');
        B[i] = (b[i - 1] == '1');
    }

    for (int i = 1; i <= m; i++) {
        C[i] = (c[i - 1] == '1');
        D[i] = (d[i - 1] == '1');
    }

    vector<vector<int>> dp(n + 2, vector<int>(m + 2, 0));
    vector<vector<int>> dp2(n + 2, vector<int>(m + 2, 0));

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (A[i] && C[j]) {
                dp[i][j] = (dp[i][j - 1] + 1) % MOD;
            }
            if (B[i] && D[j]) {
                dp2[i][j] = (dp2[i][j - 1] + 1) % MOD;
            }
        }
    }

    vector<int> cnt(n + 2, 0);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cnt[dp[i][j]]++;
            cnt[dp2[i][j]]++;
        }
    }

    int ans = 1;
    for (int i = 1; i <= n + m; i++) {
        ans = (ans * (cnt[i] + 1)) % MOD;
    }

    cout << ans << endl;

