MOD = 998244353

def add(a, b):
    a += b
    if a >= MOD:
        a -= MOD
    return a

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    if b == 0:
        return 1
    if b % 2 == 1:
        return mul(a, pow(a, b - 1))
    else:
        return pow(mul(a, a), b // 2)

def inv(a):
    return pow(a, MOD - 2)

def C(n, k):
    if k > n:
        return 0
    return mul(fact[n], mul(inv(fact[k]), inv(fact[n - k])))

