#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

typedef long long ll;

const ll INF = 1e18;

ll n, a[100005], dp[100005];

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    ll sum = 0;
    for (int i = 1; i <= n; i++) {
        sum += a[i];
    }
    dp[0] = 0;
    for (int i = 1; i <= n; i++) {
        dp[i] = INF;
        for (int j = 1; j * j <= a[i]; j++) {
            if (a[i] % j == 0) {
                ll x = j * j;
                ll y = a[i] / j * j;
                if (x <= sum) {
                    dp[i] = min(dp[i], dp[i - (y - x)] + 1);
                }
                if (y <= sum) {
                    dp[i] = min(dp[i], dp[i - (x - y)] + 1);
                }
            }
        }
    }
    cout << dp[n] << endl;
    return 0;
}
