public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        long[] a = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = sc.nextLong();
        }
        long sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += a[i];
        }
        long[] dp = new long[(int) sum + 1];
        Arrays.fill(dp, Long.MAX_VALUE);
        dp[1] = 0;
        for (int i = 1; i <= n; i++) {
            for (long j = sum; j >= a[i]; j--) {
                dp[(int) j] = Math.min(dp[(int) j], dp[(int) (j - a[i])] + 1);
            }
        }
        out.println(dp[(int) sum] - 1);
        out.close();
    }
}
