    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        p = new int[n + 1];
        x = new int[n + 1];
        f = new int[n + 1];
        g = new int[n + 1];
        h = new int[n + 1];
        c = new int[n + 1];
        d = new int[n + 1];
        e = new int[n + 1];
        a = new int[n + 1];
        b = new int[n + 1];
        for (int i = 2; i <= n; i++) {
            p[i] = sc.nextInt();
        }
        for (int i = 1; i <= n; i++) {
            x[i] = sc.nextInt();
        }
        dfs(1);
        for (int i = 1; i <= n; i++) {
            if (f[i] != 0) {
                for (int j = 1; j <= n; j++) {
                    a[j] = b[j] = 0;
                }
                a[i] = f[i];
                dfs2(1);
                if (a[1] != x[1]) {
                    ok = false;
                    break;
                }
                for (int j = 2; j <= n; j++) {
                    if (a[j] != x[j]) {
                        ok = false;
                        break;
                    }
                }
                if (!ok) break;
            }
        }
        pw.println(ok ? "POSSIBLE" : "IMPOSSIBLE");
        pw.close();
    }

    static void dfs(int u) {
        f[u] = x[u];
        g[u] = 0;
        h[u] = 0;
        for (int v = p[u]; v != 0; v = p[v]) {
            dfs(v);
            f[u] += f[v];
            g[u] += Math.max(0, f[v] - x[u]);
            h[u] += Math.max(0, x[u] - f[v]);
        }
    }

    static void dfs2(int u) {
        c[u] = 0;
        d[u] = 0;
        e[u] = 0;
        for (int v = p[u]; v != 0; v = p[v]) {
            dfs2(v);
            c[u] += c[v];
            d[u] += Math.max(0, c[v] - a[u]);
            e[u] += Math.max(0, a[u] - c[v]);
        }
        if (g[u] != 0) {
            if (g[u] > e[u]) {
                ok = false;
                return;
            }
            c[u] += g[u];
            d[u] += Math.max(0, g[u] - a[u]);
            e[u] += Math.max(0, a[u] - g[u]);
        }
        if (h[u] != 0) {
            if (h[u] > d[u]) {
                ok = false;
                return;
            }
            c[u] += h[u];
            d[u] += Math.max(0, h[u] - a[u]);
            e[u] += Math.max(0, a[u] - h[u]);
        }
        if (c[u] != f[u]) {
            ok = false;
            return;
        }
        b[u] = a[u];
        for (int v = p[u]; v != 0; v = p[v]) {
            b[u] -= c[v];
        }
        if (b[u] != x[u]) {
            ok = false;
            return;
        }
    }
