#include <bits/stdc++.h>
using namespace std;

typedef long long ll;
const int N = 200005;
const int mod = 1000000007;

int n, x[N], y[N];
int cnt[N][N], vis[N][N];
ll fac[N], inv[N];

ll qpow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % mod;
        a = a * a % mod;
        b >>= 1;
    }
    return res;
}

ll C(int n, int m) {
    if (n < m) return 0;
    return fac[n] * inv[m] % mod * inv[n - m] % mod;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= 2 * n; i++) {
        scanf("%d%d", &x[i], &y[i]);
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            cnt[i][j] = 0;
        }
    }
    for (int i = 1; i <= 2 * n; i++) {
        cnt[x[i]][y[i]]++;
    }
    fac[0] = 1;
    for (int i = 1; i <= 2 * n; i++) {
        fac[i] = fac[i - 1] * i % mod;
    }
    inv[2 * n] = qpow(fac[2 * n], mod - 2);
    for (int i = 2 * n - 1; i >= 0; i--) {
        inv[i] = inv[i + 1] * (i + 1) % mod;
    }
    ll ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (cnt[i][j] == 0) continue;
            int x = 0, y = 0;
            for (int k = 1; k <= n; k++) {
                if (cnt[k][j] == 0) continue;
                x += cnt[k][j];
                vis[k][j] = 1;
            }
            for (int k = 1; k <= n; k++) {
                if (cnt[i][k] == 0) continue;
                y += cnt[i][k];
                vis[i][k] = 1;
            }
            if (x == i && y == j) {
                ans = (ans + C(n, i) * C(n, j) % mod * fac[i + j - 2] % mod * fac[n - i] % mod * fac[n - j] % mod) % mod;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (vis[i][j]) {
                cnt[i][j] = 0;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (cnt[i][j] == 0) continue;
            int x = 0, y = 0;
            for (int k = 1; k <= n; k++) {
                if (cnt[i][k] == 0) continue;
                x += cnt[i][k];
                vis[i][k] = 1;
            }
            for (int k = 1; k <= n; k++) {
                if (cnt[k][j] == 0) continue;
                y += cnt[k][j];
                vis[k][j] = 1;
            }
            if (x == i && y == j) {
                ans = (ans + C(n, i) * C(n, j) % mod * fac[i + j - 2] % mod * fac[n - i] % mod * fac[n - j] % mod) % mod;
            }
        }
    }
    printf("%lld\n", ans);
