#include <numeric>

using namespace std;

typedef long long ll;

const int N = 200005;
const int mod = 1000000007;

int n, x[N], y[N], cnt[N], vis[N];
vector<int> v[N];

ll fac[N], inv[N];

ll qpow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % mod;
        a = a * a % mod;
        b >>= 1;
    }
    return res;
}

ll C(int n, int m) {
    if (n < m) return 0;
    return fac[n] * inv[m] % mod * inv[n - m] % mod;
}

int main() {
    cin >> n;
    for (int i = 1; i <= 2 * n; i++) {
        cin >> x[i] >> y[i];
        v[x[i]].push_back(y[i]);
    }
    for (int i = 1; i <= n; i++) {
        sort(v[i].begin(), v[i].end());
        v[i].push_back(n + 1);
    }
    fac[0] = 1;
    for (int i = 1; i <= 2 * n; i++) fac[i] = fac[i - 1] * i % mod;
    inv[2 * n] = qpow(fac[2 * n], mod - 2);
    for (int i = 2 * n - 1; i >= 1; i--) inv[i] = inv[i + 1] * (i + 1) % mod;
    ll ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (v[i][0] == j) {
                cnt[i] = 1;
                vis[j] = 1;
                break;
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (cnt[i]) {
            int p = 0;
            for (int j = 1; j <= n; j++) {
                if (!vis[j]) {
                    p++;
                }
            }
            ll res = 1;
            for (int j = 1; j <= n; j++) {
                if (vis[j]) {
                    res = res * fac[cnt[j]] % mod;
                }
            }
            res = res * fac[p] % mod;
            ans = (ans + C(2 * n - p, p)) % mod;
        }
    }
    cout << ans << endl;
    return 0;
}

import java.io.*;
import java.util.*;

public class Main {
    static final int N = 200005;
    static final int mod = 1000000007;
    static int n, ans;
    static int[] x = new int[N], y = new int[N];
    static int[] fac = new int[N], inv = new int[N];
    static int[] f = new int[N], g = new int[N];

    static int qpow(int a, int b) {
        int res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = (int) ((long) res * a % mod);
            a = (int) ((long) a * a % mod);
            b >>= 1;
        }
        return res;
    }

    static void init(int n) {
        fac[0] = 1;
        for (int i = 1; i <= n; i++) fac[i] = (int) ((long) fac[i - 1] * i % mod);
        inv[n] = qpow(fac[n], mod - 2);
        for (int i = n; i >= 1; i--) inv[i - 1] = (int) ((long) inv[i] * i % mod);
    }

    static int C(int n, int m) {
        if (n < m) return 0;
        return (int) ((long) fac[n] * inv[m] % mod * inv[n - m] % mod);
    }

    static void solve() throws IOException {
        n = readInt();
        for (int i = 1; i <= 2 * n; i++) {
            x[i] = readInt();
            y[i] = readInt();
        }
        Arrays.sort(x, 1, 2 * n + 1);
        Arrays.sort(y, 1, 2 * n + 1);
        init(2 * n);
        for (int i = 1; i <= n; i++) {
            f[i] = C(n, i);
            g[i] = C(n, i);
        }
        for (int i = 1; i <= n; i++) {
            int pos = Arrays.binarySearch(x, 1, 2 * n + 1, y[i]);
            if (pos < 0) pos = -pos - 1;
            if (pos > 0) f[i] = (f[i] - (int) ((long) f[pos - 1] * g[i - pos + 1] % mod)) % mod;
            pos = Arrays.binarySearch(y, 1, 2 * n + 1, x[i]);
            if (pos < 0) pos = -pos - 1;
            if (pos > 0) g[i] = (g[i] - (int) ((long) f[i - pos + 1] * g[pos - 1] % mod)) % mod;
        }
        for (int i = 1; i <= n; i++) ans = (ans + (int) ((long) f[i] * g[i] % mod)) % mod;
        out.println(ans);
    }

    static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    static PrintWriter out = new PrintWriter(System.out);
    static StringTokenizer st = new StringTokenizer("");

    static String nextToken() throws IOException {
        while (st == null || !st.hasMoreTokens()) st = new StringTokenizer(br.readLine());
        return st.nextToken();
    }

    static int readInt() throws IOException {
        return Integer.parseInt(nextToken());
    }

    public static void main(String[] args) throws IOException {
        solve();
        out.close();
    }
}

