import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] l = new int[n];
        int[] r = new int[n];
        for (int i = 0; i < n; i++) {
            l[i] = scanner.nextInt();
            r[i] = scanner.nextInt();
        }
        int[] tree = new int[4 * 100000 + 1];
        buildTree(tree, 1, 1, 100000, l, r);
        System.out.println(queryTree(tree, 1, 1, 100000));
    }

    public static void buildTree(int[] tree, int node, int start, int end, int[] l, int[] r) {
        if (start == end) {
            tree[node] = 1;
            return;
        }
        int mid = (start + end) / 2;
        buildTree(tree, 2 * node, start, mid, l, r);
        buildTree(tree, 2 * node + 1, mid + 1, end, l, r);
        tree[node] = tree[2 * node] + tree[2 * node + 1];
    }

    public static int queryTree(int[] tree, int node, int start, int end) {
        return tree[node];
    }

    public static void updateTree(int[] tree, int node, int start, int end, int index, int value) {
        if (start == end) {
            tree[node] = value;
            return;
        }
        int mid = (start + end) / 2;
        if (index <= mid) {
            updateTree(tree, 2 * node, start, mid, index, value);
        } else {
            updateTree(tree, 2 * node + 1, mid + 1, end, index, value);
        }
        tree[node] = tree[2 * node] + tree[2 * node + 1];
    }
}
