    for (int i = 1; i <= r; i++) scanf("%d", &a[i]);
    for (int i = 1; i <= m; i++) scanf("%d%d%d", &a[i + r], &b[i + r], &c[i + r]);
    for (int i = 1; i <= n; i++) for (int j = 1; j <= n; j++) dis[i][j] = 1e9;
    for (int i = 1; i <= m; i++) dis[a[i + r]][b[i + r]] = dis[b[i + r]][a[i + r]] = c[i + r];
    for (int k = 1; k <= n; k++) for (int i = 1; i <= n; i++) for (int j = 1; j <= n; j++)
        dis[i][j] = min(dis[i][j], dis[i][k] + dis[k][j]);
    for (int i = 1; i <= n; i++) vis[i] = 1;
    for (int i = 1; i <= r; i++) vis[a[i]] = 0;
    memset(dp, 0x3f, sizeof(dp));
    dp[0][(1 << r) - 1] = 0;
    for (int i = 0; i < (1 << r); i++) {
        for (int j = 1; j <= n; j++) if (vis[j]) {
