            for (int k = 0; k < r; k++) if ((i >> k) & 1) {
                nxt = j;
                for (int l = k + 1; l < r; l++) if (!(i >> l) & 1) {
                    dp[j][i | (1 << l)] = min(dp[j][i | (1 << l)], dp[nxt][i] + dis[nxt][a[l + 1]]);
                }
            }
        }
    }
    int ans = 1e9;
    for (int i = 0; i < (1 << r); i++) {
        for (int j = 1; j <= n; j++) if (vis[j]) {
            ans = min(ans, dp[j][i] + dis[j][a[1]] + dis[a[r]][j]);
        }
    }
