import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;
import java.util.StringTokenizer;

public class Main {
    static int N, M, R;
    static int[] r = new int[10];
    static int[][] A = new int[210][210];
    static int[][] C = new int[210][210];
    static int[][] dp = new int[1 << 10][10];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        R = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= R; i++) {
            r[i] = Integer.parseInt(st.nextToken());
        }
        for (int i = 1; i <= M; i++) {
            st = new StringTokenizer(br.readLine());
            int a = Integer.parseInt(st.nextToken());
            int b = Integer.parseInt(st.nextToken());
            int c = Integer.parseInt(st.nextToken());
            A[a][b] = A[b][a] = c;
        }
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= N; j++) {
                for (int k = 1; k <= N; k++) {
                    C[i][j] = Math.min(C[i][j], A[i][k] + A[k][j]);
                }
            }
        }
        for (int i = 1; i <= N; i++) {
            dp[1 << (i - 1)][i] = 0;
        }
        for (int i = 1; i < (1 << R); i++) {
            for (int j = 1; j <= R; j++) {
                if (((i >> (j - 1)) & 1) == 0) {
                    continue;
                }
                for (int k = 1; k <= R; k++) {
                    if (k == j) {
                        continue;
                    }
                    if (((i >> (k - 1)) & 1) == 0) {
                        continue;
                    }
                    int tmp = dp[i][j];
                    dp[i][j] = Math.min(dp[i][j], dp[i ^ (1 << (j - 1))][k] + C[r[k]][r[j]]);
                    dp[i ^ (1 << (j - 1))][k] = Math.min(dp[i ^ (1 << (j - 1))][k], tmp + C[r[j]][r[k]]);
                }
            }
        }
        int ans = Integer.MAX_VALUE;
        for (int i = 1; i <= R; i++) {
            ans = Math.min(ans, dp[(1 << R) - 1][i] + C[r[i]][r[1]] + C[r[1]][r[i]]);
        }
        System.out.println(ans);
    }
}
