#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

const int mod = 998244353;

struct Point {
    int x, y;
};

bool comparePoints(const Point& a, const Point& b) {
    if (a.x == b.x) return a.y < b.y;
    return a.x < b.x;
}

int crossProduct(const Point& a, const Point& b, const Point& c) {
    return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
}

int orientation(const Point& a, const Point& b, const Point& c) {
    return crossProduct(a, b, c);
}

int convexHull(vector<Point>& points) {
    int n = points.size();
    if (n < 3) return n;

    sort(points.begin(), points.end(), comparePoints);

    vector<Point> hull;
    hull.push_back(points[0]);
    hull.push_back(points[1]);

    for (int i = 2; i < n; i++) {
        while (hull.size() > 1 && orientation(hull[hull.size() - 2], hull.back(), points[i]) <= 0) {
            hull.pop_back();
        }
        hull.push_back(points[i]);
    }

    return hull.size();
}

int main() {
    int n;
    cin >> n;

    vector<Point> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].x >> points[i].y;
    }

    int ans = 0;
    for (int i = 0; i < (1 << n); i++) {
        vector<Point> hull;
        for (int j = 0; j < n; j++) {
            if (i & (1 << j)) {
                hull.push_back(points[j]);
            }
        }

        int size = convexHull(hull);
        int cnt = n - size;
        int score = 1;
        for (int j = 0; j < cnt; j++) {
            score = (score * 2) % mod;
        }
        ans = (ans + score) % mod;
    }

    cout << ans << endl;

    return 0;
}
