import sys
input = sys.stdin.readline

MOD = 998244353

def cross(p1, p2, p3):
    return (p2[0] - p1[0]) * (p3[1] - p1[1]) - (p2[1] - p1[1]) * (p3[0] - p1[0])

def orientation(p1, p2, p3):
    return cross(p1, p2, p3)

def convex_hull(points):
    n = len(points)
    if n < 3:
        return points

    points.sort(key=lambda x: (x[0], x[1]))
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    q = 0
    hull = [points[p]]
    while True:
        r = (q + 1) % n
        if orientation(points[p], points[q], points[r]) > 0:
            hull.append(points[r])
            p = r
        else:
            q = r
        if p == l:
            break
    return hull

def get_score(points, i, j):
    hull = convex_hull(points)
    n = len(hull)
    inside = 0
    for p in points:
        if p in hull:
            continue
        ok = False
        for k in range(n):
            if (orientation(p, hull[k], hull[(k + 1) % n]) > 0 and
                    orientation(hull[k], p, hull[(k + 1) % n]) > 0):
                ok = True
                break
        if not ok:
            inside += 1
    return pow(2, inside - (j - i + 1)) % MOD

def main():
    n = int(input())
    points = []
    for _ in range(n):
        x, y = map(int, input().split())
        points.append((x, y))
    dp = [0] * (n + 1)
    for i in range(n):
        for j in range(i + 1, n + 1):
            dp[j] = (dp[j] + get_score(points, i, j - 1)) % MOD
    print(sum(dp) % MOD)

main()
