        return (x[i] - x[k]) * (y[j] - y[k]) - (x[j] - x[k]) * (y[i] - y[k]);
    }

    static int lower_bound(int l, int r, int val) {
        while (l < r) {
            int mid = (l + r) >> 1;
            if (val <= cross(ord[mid], ord[mid + 1], 0)) r = mid;
            else l = mid + 1;
        }
        return r;
    }

    static int upper_bound(int l, int r, int val) {
        while (l < r) {
            int mid = (l + r + 1) >> 1;
            if (val >= cross(ord[mid], ord[mid + 1], 0)) l = mid;
            else r = mid - 1;
        }
        return l;
    }

    static void build(int l, int r, int p) {
        if (l == r) {
            st[p] = l;
            return;
        }
        int mid = (l + r) >> 1;
        build(l, mid, p * 2);
        build(mid + 1, r, p * 2 + 1);
        st[p] = st[p * 2 + 1];
        if (cross(ord[st[p * 2]], ord[st[p * 2] + 1], 0) < cross(ord[st[p * 2 + 1]], ord[st[p * 2 + 1] + 1], 0)) st[p] = st[p * 2];
    }

    static int query(int l, int r, int p, int x, int y) {
        if (l > y || r < x) return -1;
        if (l >= x && r <= y) return st[p];
        int mid = (l + r) >> 1;
        int res = query(l, mid, p * 2, x, y);
        if (res != -1) return res;
        return query(mid + 1, r, p * 2 + 1, x, y);
    }

