        int cnt = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || x[i] != x[i - 1]) cnt++;
        }
        for (int i = 0; i < n; i++) {
            if (i == 0 || y[i] != y[i - 1]) cnt++;
        }
        ord = new int[cnt];
        int idx = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || x[i] != x[i - 1]) {
                for (int j = 0; j < n; j++) {
                    if (x[i] == p[j][0] && y[i] == p[j][1]) {
                        ord[idx++] = j;
                        break;
                    }
                }
            }
        }
        for (int i = 0; i < n; i++) {
            if (i == 0 || y[i] != y[i - 1]) {
                for (int j = 0; j < n; j++) {
                    if (x[i] == p[j][0] && y[i] == p[j][1]) {
                        ord[idx++] = j;
                        break;
                    }
                }
            }
        }
        st = new int[4 * cnt];
        build(0, cnt - 1, 1);
        long ans = 0;
        for (int i = 0; i < cnt; i++) {
            int l = i, r = i;
            while (true) {
                int j = query(0, cnt - 1, 1, l, r);
                if (j == -1) break;
                int k = query(0, cnt - 1, 1, 0, j - 1);
                if (k == -1) {
                    l = j;
                    r = j;
                    continue;
                }
                int m = query(0, cnt - 1, 1, j + 1, cnt - 1);
                if (m == -1) {
                    l = j;
                    r = j;
                    continue;
                }
                int res = 0;
                for (int t = 0; t < cnt; t++) {
                    if (t == j || t == k || t == m) continue;
                    int val = cross(ord[t], ord[j], 0);
                    if (val >= 0) res++;
                }
                ans = (ans + (1L << (res - (j - k + 1)))) % MOD;
                l = k;
                r = m;
            }
        }
        pw.println(ans);
        pw.close();
    }

