    scanf("%d", &K);
    for (int i = 0; i < K; i++) {
        scanf("%d", &r[i]);
        n++;
    }
    scanf("%d", &Q);
    for (int i = 0; i < Q; i++) {
        scanf("%d %d", &t[i], &a[i]);
        n++;
    }

    int ans[MAXN];
    for (int i = 0; i < Q; i++) {
        int t_i = t[i];
        int a_i = a[i];
        int ans_i = 0;

        int j = 0;
        while (j < K && r[j] < t_i) {
            ans_i += (r[j + 1] - r[j]) * (X - a_i);
            j++;
        }

        if (j == K) {
            ans_i = X;
        } else {
            int k = j;
            while (k >= 0 && r[k] <= t_i) {
                ans_i += (t_i - r[k]) * (X - a_i);
                k--;
            }
            ans_i = (X - a_i) * (t_i - r[j]) + a_i;
        }

        ans[i] = ans_i;
    }

    for (int i = 0; i < Q; i++) {
        printf("%d\n", ans[i]);
    }

    return 0;
