#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

struct Point {
    long long x, y;
};

struct Event {
    long long x, y, id;
    bool isStreet;
};

bool compareEvents(const Event& a, const Event& b) {
    if (a.x != b.x) return a.x < b.x;
    return a.isStreet < b.isStreet;
}

long long dist(const Point& a, const Point& b) {
    return abs(a.x - b.x) + abs(a.y - b.y);
}

long long solve(vector<Point>& points, vector<Event>& events) {
    sort(events.begin(), events.end(), compareEvents);
    long long ans = 0;
    long long x = events[0].x;
    long long y = events[0].y;
    long long id = events[0].id;
    for (int i = 0; i < events.size(); i++) {
        while (i < events.size() - 1 && events[i + 1].x == x) {
            if (events[i + 1].isStreet) {
                ans += dist(points[id], points[events[i + 1].id]);
                id = events[i + 1].id;
            }
            i++;
        }
        ans += dist({x, y}, events[i].isStreet ? points[id] : points[events[i].id]);
        x = events[i].x;
        y = events[i].y;
        id = events[i].id;
    }
    return ans;
}

int main() {
    long long x1, y1, x2, y2;
    cin >> x1 >> y1 >> x2 >> y2;
    int n;
    cin >> n;
    vector<Point> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].x >> points[i].y;
    }
    vector<Event> events;
    for (int i = 0; i < n; i++) {
        events.push_back({points[i].x, points[i].y, i, true});
        events.push_back({points[i].y, points[i].x, i, false});
    }
    events.push_back({x1, y1, -1, true});
    events.push_back({y1, x1, -1, false});
    events.push_back({x2, y2, -1, true});
    events.push_back({y2, x2, -1, false});
    sort(events.begin(), events.end(), compareEvents);
    long long ans = solve(points, events);
    cout << fixed << setprecision(15) << ans << endl;
    return 0;
}
