import sys
import math

class Node:
    def __init__(self, x, y):
        self.x = x
        self.y = y

class Edge:
    def __init__(self, u, v, w):
        self.u = u
        self.v = v
        self.w = w

def dist(u, v):
    return math.sqrt((u.x - v.x)**2 + (u.y - v.y)**2)

def kruskal(n, m, edges):
    parent = [i for i in range(n)]
    rank = [0] * n
    result = []

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        rootx = find(x)
        rooty = find(y)
        if rootx != rooty:
            if rank[rootx] > rank[rooty]:
                parent[rooty] = rootx
            else:
                parent[rootx] = rooty
                if rank[rootx] == rank[rooty]:
                    rank[rooty] += 1

    edges.sort(key=lambda x: x.w)
    for e in edges:
        u = e.u
        v = e.v
        if find(u) != find(v):
            result.append(e)
            union(u, v)

    return result

def dijkstra(n, edges, start, end):
    dist = [float('inf')] * n
    dist[start] = 0
    visited = [False] * n

    for _ in range(n):
        u = -1
        for i in range(n):
            if not visited[i] and (u == -1 or dist[i] < dist[u]):
                u = i
        visited[u] = True

        for e in edges:
            v = e.u if e.v == u else e.v
            if dist[u] + e.w < dist[v]:
                dist[v] = dist[u] + e.w

    return dist[end]

def main():
    input = sys.stdin.readline
    x1, y1, x2, y2 = map(int, input().split())
    n = int(input())
    fountains = [Node(int(x), int(y)) for x, y in (map(int, input().split()) for _ in range(n))]

    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            u = fountains[i]
            v = fountains[j]
            edges.append(Edge(i, j, dist(u, v)))

    for i in range(n):
        for j in range(n):
            if i != j:
                u = fountains[i]
                v = fountains[j]
                edges.append(Edge(i, j, dist(u, v) + 200))

    edges2 = kruskal(n, n, edges)
    edges3 = []
    for e in edges2:
        u = e.u
        v = e.v
        edges3.append(Edge(u, v, dist(fountains[u], fountains[v]) + 200))

    print(dijkstra(n, edges3, 0, n - 1) + dist(Node(x1, y1), Node(x2, y2)))

if __name__ == "__main__":
    main()
