import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class Node implements Comparable<Node> {
        int x, y, id;

        public Node(int x, int y, int id) {
            this.x = x;
            this.y = y;
            this.id = id;
        }

        @Override
        public int compareTo(Node o) {
            return Integer.compare(x, o.x);
        }
    }

    static class Edge {
        int u, v, w;

        public Edge(int u, int v, int w) {
            this.u = u;
            this.v = v;
            this.w = w;
        }
    }

    static int find(int[] fa, int x) {
        return fa[x] == x ? x : (fa[x] = find(fa, fa[x]));
    }

    static void merge(int[] fa, int[] sz, int u, int v) {
        u = find(fa, u);
        v = find(fa, v);
        if (u == v) return;
        if (sz[u] < sz[v]) {
            int temp = u;
            u = v;
            v = temp;
        }
        fa[v] = u;
        sz[u] += sz[v];
    }

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(in.readLine());
        int x1 = Integer.parseInt(st.nextToken());
        int y1 = Integer.parseInt(st.nextToken());
        int x2 = Integer.parseInt(st.nextToken());
        int y2 = Integer.parseInt(st.nextToken());
        int n = Integer.parseInt(in.readLine());
        Node[] a = new Node[n + 1];
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(in.readLine());
            a[i] = new Node(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()), i);
        }
        Arrays.sort(a, 1, n + 1);
        int[] fa = new int[n + 1];
        int[] sz = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            fa[i] = i;
            sz[i] = 1;
        }
        List<Edge>[] e = new List[n + 1];
        for (int i = 1; i <= n; i++) {
            e[i] = new ArrayList<>();
        }
        for (int i = 1; i <= n; i++) {
            for (int j = i + 1; j <= n; j++) {
                if (a[i].x == a[j].x) {
                    e[i].add(new Edge(i, j, Math.abs(a[i].y - a[j].y) * 100));
                    e[j].add(new Edge(j, i, Math.abs(a[i].y - a[j].y) * 100));
                } else if (a[i].y == a[j].y) {
                    e[i].add(new Edge(i, j, Math.abs(a[i].x - a[j].x) * 100));
                    e[j].add(new Edge(j, i, Math.abs(a[i].x - a[j].x) * 100));
                }
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (find(fa, i) != find(fa, j)) {
                    merge(fa, sz, i, j);
                    e[find(fa, i)].add(new Edge(find(fa, i), find(fa, j), Math.abs(a[i].x - a[j].x) * 100 + Math.abs(a[i].y - a[j].y) * 100));
                }
            }
        }
        int[] dis = new int[n + 1];
        Arrays.fill(dis, Integer.MAX_VALUE / 2);
        dis[find(fa, 1)] = 0;
        PriorityQueue<Edge> q = new PriorityQueue<>((o1, o2) -> o1.w - o2.w);
        q.offer(new Edge(1, 1, 0));
        while (!q.isEmpty()) {
            Edge edge = q.poll();
            for (Edge next : e[edge.u]) {
                if (dis[next.v] > dis[edge.u] + next.w) {
                    dis[next.v] = dis[edge.u] + next.w;
                    q.offer(new Edge(next.v, next.v, dis[next.v]));
                }
            }
        }
        out.println(dis[find(fa, n)] + Math.abs(x1 - x2) * 100 + Math.abs(y1 - y2) * 100);
        out.close();
    }
}
