def min_operations(A, B):
    n = len(A)
    if A == B:
        return 0
    if n == 1:
        return -1 if A != B else 0
    if n == 2:
        if A == B:
            return 0
        if A[0] == B[0] == '1':
            return 2
        return -1

    diff = [i for i in range(n) if A[i] != B[i]]
    if len(diff) == 0:
        return 0
    if len(diff) == 1:
        return -1 if A[diff[0]] != B[diff[0]] else 1

    min_ops = float('inf')
    for i in range(n):
        A_rotated = A[i:] + A[:i]
        ops = 0
        for j in range(n):
            if A_rotated[j] != B[j]:
                ops += 1
        min_ops = min(min_ops, ops)

    return min_ops

