                cnt[1]--;
                cnt[0]++;
            }
            if (cnt[1] == 0) break;
        }
        int t = 1;
        for (int j = 1; j <= p; j++) t = (long long)t * (cnt[1] + j) % mod;
        for (int j = 1; j <= q; j++) t = (long long)t * (cnt[0] + j) % mod;
        t = (long long)t * C(p + q, p) % mod;
        ans = (ans + t) % mod;
    }
    ans = (long long)ans * fac[cnt[1]] % mod * fac[cnt[1]] % mod;
    printf("%d\n", ans);
    return 0;
}

