    for (int i = 1; i <= k; i++) {
        fac[i] = (1LL * fac[i - 1] * i) % MOD;
    }

    vector<vector<int>> dp(k + 1, vector<int>(k + 1));
    dp[0][0] = 1;
    for (int i = 0; i < k; i++) {
        for (int j = 0; j <= i; j++) {
            dp[i + 1][j + 1] = (dp[i + 1][j + 1] + 1LL * dp[i][j] * (k - i)) % MOD;
            if (pos_a[i] == pos_b[j]) {
                dp[i + 1][j] = (dp[i + 1][j] + 1LL * dp[i][j] * (k - i)) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= k; i++) {
        ans = (ans + 1LL * dp[k][i] * fac[i] % MOD * fac[k - i] % MOD * fac[k] % MOD * fac[k]) % MOD;
    }

    cout << ans << endl;
    return 0;
}
