import java.io.*;
import java.util.*;

public class Main {
    static final int mod = 998244353;
    static final int N = 10010;
    static int n, m, ans;
    static int[] a = new int[N], b = new int[N], fac = new int[N], inv = new int[N];
    static int[][] c = new int[N][N];

    static int ksm(int x, int y) {
        int res = 1;
        while (y > 0) {
            if ((y & 1) == 1) res = (int) ((long) res * x % mod);
            x = (int) ((long) x * x % mod);
            y >>= 1;
        }
        return res;
    }

    static int C(int x, int y) {
        if (x < y) return 0;
        return (int) ((long) fac[x] * inv[y] % mod * inv[x - y] % mod);
    }

    static void init() {
        fac[0] = 1;
        for (int i = 1; i <= n; i++) fac[i] = (int) ((long) fac[i - 1] * i % mod);
        inv[n] = ksm(fac[n], mod - 2);
        for (int i = n - 1; i >= 0; i--) inv[i] = (int) ((long) inv[i + 1] * (i + 1) % mod);
    }

    static void solve() throws IOException {
        n = nextInt();
        for (int i = 1; i <= n; i++) a[i] = nextInt();
        for (int i = 1; i <= n; i++) b[i] = nextInt();
        int cnt = 0;
        for (int i = 1; i <= n; i++) if (a[i] == 1) cnt++;
        for (int i = 1; i <= n; i++) if (b[i] == 1) cnt--;
        if (cnt != 0) {
            out.println(0);
            return;
        }
        init();
        for (int i = 1; i <= n; i++) c[i][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= i; j++) c[i][j] = (c[i - 1][j - 1] + c[i - 1][j]) % mod;
        }
        int res = 1;
        for (int i = 1; i <= n; i++) {
            res = (int) ((long) res * C(n - 1, i - 1) % mod * c[i - 1][i - 1] % mod * c[i - 1][i - 1] % mod);
        }
        res = (int) ((long) res * ksm(n, 2) % mod);
        out.println(res);
    }

    static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    static PrintWriter out = new PrintWriter(System.out);
    static StringTokenizer in = new StringTokenizer("");

    static String nextToken() throws IOException {
        while (!in.hasMoreTokens()) in = new StringTokenizer(br.readLine());
        return in.nextToken();
    }

    static int nextInt() throws IOException {
        return Integer.parseInt(nextToken());
    }

    public static void main(String[] args) throws IOException {
        solve();
        out.close();
    }
}

