            ret = (ret + dfs(i - 1, j, 1, 1)) % MOD;
        }
    }
    if (j > 0 && s1[i - 1] == s1[j - 1] && s2[i - 1] == s2[j - 1]) {
        if (c1 == 0 || c2 == 0) {
            ret = (ret + dfs(i, j - 1, c1, c2)) % MOD;
        }
        if (c1 == 0 && c2 == 0) {
            ret = (ret + dfs(i, j - 1, 1, 1)) % MOD;
        }
    }
    if (i > 0 && s1[i - 1] == s2[i - 1] && s2[i - 1] == s1[j - 1]) {
        if (c1 == 0) {
            ret = (ret + dfs(i - 1, j, c1, 1)) % MOD;
        }
        if (c2 == 0) {
            ret = (ret + dfs(i, j - 1, 1, c2)) % MOD;
        }
    }
    return ret;
}

int main() {
    int n;
    scanf("%d %s %s", &n, s1, s2);
    memset(dp, -1, sizeof(dp));
    int ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (s1[i] == s1[j] && s2[i] == s2[j]) {
                ans = (ans + dfs(i + 1, j + 1, 1, 1)) % MOD;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
