#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXN 100005

int cmp(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    int n;
    scanf("%d", &n);
    int a[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), cmp);
    long long ans = 0;
    for (int i = 0; i < n - 3; i++) {
        for (int j = i + 1; j < n - 2; j++) {
            for (int k = j + 1; k < n - 1; k++) {
                for (int l = k + 1; l < n; l++) {
                    long long area = (long long)a[i] * a[j];
                    if (a[k] == a[l]) {
                        area = (long long)a[i] * a[j] * a[k];
                    } else if (a[k] * a[l] == a[i] * a[j]) {
                        area = (long long)a[i] * a[k];
                    } else if (a[k] * a[l] == a[j] * a[i]) {
                        area = (long long)a[j] * a[l];
                    } else if (a[k] * a[l] > a[i] * a[j]) {
                        area = (long long)a[k] * a[l];
                    }
                    ans = max(ans, area);
                }
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
