#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007

int dp[55][55][3][3];
char s1[105], s2[105];

int dfs(int i, int j, int c1, int c2) {
    if (i == j) return 1;
    if (dp[i][j][c1][c2] != -1) return dp[i][j][c1][c2];
    int ans = 0;
    if (c1 == 0) {
        if (s1[i] == s2[j - 1]) {
            ans = (ans + dfs(i + 1, j, 1, 1)) % MOD;
            ans = (ans + dfs(i, j - 1, 1, 2)) % MOD;
        } else {
            ans = (ans + dfs(i + 1, j, 1, 2)) % MOD;
            ans = (ans + dfs(i, j - 1, 1, 1)) % MOD;
        }
    } else if (c1 == 1) {
        if (s1[i] == s2[j - 1]) {
            ans = (ans + dfs(i + 1, j, 2, 2)) % MOD;
            ans = (ans + dfs(i, j - 1, 2, 1)) % MOD;
        } else {
            ans = (ans + dfs(i + 1, j, 2, 1)) % MOD;
            ans = (ans + dfs(i, j - 1, 2, 2)) % MOD;
        }
    } else {
        if (s1[i] == s2[j - 1]) {
            ans = (ans + dfs(i + 1, j, 2, 1)) % MOD;
            ans = (ans + dfs(i, j - 1, 0, 1)) % MOD;
        } else {
            ans = (ans + dfs(i + 1, j, 2, 1)) % MOD;
            ans = (ans + dfs(i, j - 1, 0, 1)) % MOD;
        }
    }
    dp[i][j][c1][c2] = ans;
    return ans;
}

int main() {
    int n;
    scanf("%d", &n);
    scanf("%s", s1);
    scanf("%s", s2);
    memset(dp, -1, sizeof dp);
    int ans = dfs(0, n, 0, 0);
    printf("%d\n", ans);
    return 0;
}
