        if (i == 0) {
            dp[i][0] = 1;
            dp[i][1] = 1;
            dp[i][2] = 1;
        } else {
            int c1 = s1[i] == s1[i - 1] ? 0 : 1;
            int c2 = s2[i] == s2[i - 1] ? 0 : 1;
            int c3 = s1[i] == s2[i - 1] ? 0 : 1;
            int c4 = s2[i] == s1[i - 1] ? 0 : 1;
            for (int j = 0; j < 3; j++) {
                dp[i][j] = add(dp[i - 1][(j + 1) % 3], dp[i - 1][(j + 2) % 3]);
                if (c1 && j == 0) dp[i][j] = 0;
                if (c2 && j == 1) dp[i][j] = 0;
                if (c3 && j == 2) dp[i][j] = 0;
                if (c4 && j == 0) dp[i][j] = 0;
            }
        }
    }
    int res = 0;
    for (int i = 0; i < 3; i++) {
        res = add(res, dp[n - 1][i]);
    }
