#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 100005
#define M 100005

typedef struct {
    int to, next, w;
} Edge;

int n, m, k, q, cnt;
int head[N], dis[N], dep[N], fa[N][20];
Edge e[M];

void add(int u, int v, int w) {
    e[++cnt].to = v;
    e[cnt].next = head[u];
    e[cnt].w = w;
    head[u] = cnt;
}

void dfs(int u, int f) {
    fa[u][0] = f;
    dep[u] = dep[f] + 1;
    for (int i = 1; i <= 18; i++) {
        fa[u][i] = fa[fa[u][i - 1]][i - 1];
    }
    for (int i = head[u]; i; i = e[i].next) {
        int v = e[i].to;
        if (v == f) continue;
        dis[v] = dis[u] + e[i].w;
        dfs(v, u);
    }
}

int lca(int u, int v) {
    if (dep[u] < dep[v]) {
        int temp = u;
        u = v;
        v = temp;
    }
    for (int i = 18; i >= 0; i--) {
        if (dep[fa[u][i]] >= dep[v]) {
            u = fa[u][i];
        }
    }
    if (u == v) return u;
    for (int i = 18; i >= 0; i--) {
        if (fa[u][i] != fa[v][i]) {
            u = fa[u][i];
            v = fa[v][i];
        }
    }
    return fa[u][0];
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        add(u, v, w);
        add(v, u, w);
    }
    scanf("%d%d", &m, &k);
    dfs(k, 0);
    for (int i = 1; i <= m; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        int l = lca(u, v);
        printf("%d\n", dis[u] + dis[v] - 2 * dis[l]);
    }
    return 0;
}
