int dis[MAXN], fa[MAXN], dep[MAXN];

void addEdge(int u, int v, int w) {
    edges[++cnt].to = v;
    edges[cnt].next = head[u];
    edges[cnt].w = w;
    head[u] = cnt;
}

void dfs(int u, int f) {
    fa[u] = f;
    dep[u] = dep[f] + 1;
    for (int i = head[u]; i; i = edges[i].next) {
        int v = edges[i].to;
        if (v != f) {
            dis[v] = dis[u] + edges[i].w;
            dfs(v, u);
        }
    }
}

