#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAXN = 105;
const int MAXM = 10005;

int h, w, n;
int a[MAXN];
int cnt[MAXN];
int vis[MAXN][MAXN];
int color[MAXN][MAXN];
int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

void bfs(int x, int y, int c) {
    queue<pair<int, int>> q;
    q.push({x, y});
    vis[x][y] = 1;
    color[x][y] = c;
    while (!q.empty()) {
        int x = q.front().first;
        int y = q.front().second;
        q.pop();
        for (int i = 0; i < 4; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            if (nx >= 1 && nx <= h && ny >= 1 && ny <= w && !vis[nx][ny] && a[nx * w + ny] > 0) {
                vis[nx][ny] = 1;
                color[nx][ny] = c;
                q.push({nx, ny});
                a[nx * w + ny]--;
            }
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cin >> h >> w;
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    for (int i = 1; i <= n; i++) {
        cnt[i] = a[i];
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            int c = 0;
            for (int k = 1; k <= n; k++) {
                if (cnt[k] > 0) {
                    c = k;
                    break;
                }
            }
            if (cnt[c] > 0) {
                bfs(i, j, c);
            }
        }
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            cout << color[i][j] << " ";
        }
        cout << "\n";
    }
    return 0;
}
