```c
#include <stdio.h>
#include <stdlib.h>

#define MAX_N 105
#define MAX_H 105
#define MAX_W 105

int h, w, n, a[MAX_N], cnt[MAX_N], x[MAX_N], y[MAX_N], vis[MAX_H][MAX_W];
int dx[] = {-1, 0, 1, 0};
int dy[] = {0, -1, 0, 1};

void dfs(int i, int j) {
    if (i < 1 || i > h || j < 1 || j > w || vis[i][j] || cnt[a[i * w + j]] == 0) return;
    vis[i][j] = 1;
    cnt[a[i * w + j]]--;
    for (int k = 0; k < 4; k++) {
        dfs(i + dx[k], j + dy[k]);
    }
}

int main() {
    scanf("%d%d%d", &h, &w, &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    int sum = 0;
    for (int i = 1; i <= n; i++) {
        sum += a[i];
    }
    if (sum != h * w) {
        printf("Error: Sum of areas is not equal to H * W\n");
        return 0;
    }
    int id = 1;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            x[id] = i;
            y[id] = j;
            id++;
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= h * w; j++) {
            if (cnt[i] == 0) continue;
            cnt[i]--;
            dfs(x[j], y[j]);
            cnt[i]++;
        }
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            printf("%d ", a[i * w + j]);
        }
        printf("\n");
    }
    return 0;
}
