#include <queue>
#include <algorithm>

using namespace std;

int main() {
    int h, w;
    cin >> h >> w;

    int n;
    cin >> n;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<vector<int>> grid(h, vector<int>(w, 0));
    int cnt = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (cnt < n) {
                grid[i][j] = cnt + 1;
                cnt++;
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (a[grid[i][j] - 1] > 1) {
                queue<pair<int, int>> q;
                q.push({i, j});
                int color = grid[i][j];
                a[color - 1]--;
                while (!q.empty()) {
                    int x = q.front().first;
                    int y = q.front().second;
                    q.pop();
                    if (x > 0 && grid[x - 1][y] == 0) {
                        grid[x - 1][y] = color;
                        a[color - 1]--;
                        q.push({x - 1, y});
                    }
                    if (x < h - 1 && grid[x + 1][y] == 0) {
                        grid[x + 1][y] = color;
                        a[color - 1]--;
                        q.push({x + 1, y});
                    }
                    if (y > 0 && grid[x][y - 1] == 0) {
                        grid[x][y - 1] = color;
                        a[color - 1]--;
                        q.push({x, y - 1});
                    }
                    if (y < w - 1 && grid[x][y + 1] == 0) {
                        grid[x][y + 1] = color;
                        a[color - 1]--;
                        q.push({x, y + 1});
                    }
                }
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            cout << grid[i][j] << " ";
        }
        cout << endl;
    }

    return 0;
}
