#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100
#define MAX 10000000

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) return 0;
    }
    return 1;
}

int main() {
    int n;
    scanf("%d", &n);
    int x[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &x[i]);
    }
    int ans = 0;
    for (int i = 0; i < n; i++) {
        int p = x[i];
        while (p <= MAX) {
            if (is_prime(p)) {
                int cnt = 0;
                int j = i;
                while (j < n && x[j] <= p * 2) {
                    cnt++;
                    j++;
                }
                if (cnt > 0) {
                    ans++;
                    i = j - 1;
                    break;
                }
            }
            p++;
        }
    }
    printf("%d\n", ans);
    return 0;
}
