    while (current != 1) {
        visited.insert(current);
        bool found = false;
        for (int neighbor : graph[current]) {
            if (neighbor != 1 && visited.find(neighbor) == visited.end()) {
                found = true;
                current = neighbor;
                break;
            }
        }
        if (!found) {
            break;
        }
        steps++;
    }

    if (steps == 0) {
        cout << "IMPOSSIBLE" << endl;
    } else {
        cout << steps + 1 << "POSSIBLE" << endl;
    }

    return 0;
